private function decodeGetParam($value, $flags = null, $encoding = null) {
	if(is_array($value)) {
		foreach($value as $key => $val) {
			$value[$key] = $this->decodeGetParam($val, $flags, $encoding);
		}
	} else {
		$value = html_entity_decode($value, $flags, $encoding);
	}
	return $value;
}
